CREATE SCHEMA IF NOT EXISTS trakt;
USE trakt;

DROP TABLE IF  EXISTS watched_movies;

CREATE TABLE IF NOT EXISTS watched_movies (
    `id` INT NOT NULL AUTO_INCREMENT PRIMARY KEY,
    `last_watched_at` VARCHAR(24) CHARACTER SET utf8,
    `last_updated_at` VARCHAR(24) CHARACTER SET utf8,
    `status` VARCHAR(16) CHARACTER SET utf8,
    `type` VARCHAR(16) CHARACTER SET utf8,
    `title` VARCHAR(66) CHARACTER SET utf8,
    `year` INT,
    `ids_trakt` INT,
    `ids_slug` VARCHAR(67) CHARACTER SET utf8,
    `ids_imdb` VARCHAR(9) CHARACTER SET utf8,
    `ids_tmdb` INT
);

UPDATE watched_movies
SET
    last_watched_at = DATE_FORMAT(STR_TO_DATE(last_watched_at,'%Y-%m-%dT%H:%i:%s.000Z'),'%Y-%m-%d %H:%i:%s'),
    last_updated_at = DATE_FORMAT(STR_TO_DATE(last_updated_at,'%Y-%m-%dT%H:%i:%s.000Z'),'%Y-%m-%d %H:%i:%s')
;

ALTER TABLE watched_movies MODIFY COLUMN last_watched_at DATETIME;
ALTER TABLE watched_movies MODIFY COLUMN last_updated_at DATETIME;
